import { moduleMetadata } from '@storybook/angular';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { UserDropdownComponent } from './user-dropdown.component';
import { AvatarModule } from 'primeng/avatar';
import { OverlayPanelModule } from 'primeng/overlaypanel';
import { MenuModule } from 'primeng/menu';
import { PrimeIcons } from 'primeng/api';
const meta = {
    title: 'Navigation/UserDropdown',
    component: UserDropdownComponent,
    tags: ['autodocs'],
    decorators: [moduleMetadata({ imports: [AvatarModule, OverlayPanelModule, BrowserAnimationsModule, MenuModule] })],
    argTypes: {
        wrapperClassNames: { description: 'Custom CSS classes as component wraps' },
        classNames: { description: 'Custom CSS classes' },
        name: { description: 'The name of the user' },
        email: { description: 'The email of the user' },
        image: { description: 'The avatar image path of the user' },
        menu: { description: 'The dropdown menu items list' },
    },
    parameters: {
        docs: {
            description: {
                component: `Component that uses [PrimeNg Avatar](https://primeng.org/avatar) and 
							[PrimeNg Overlay Panel](https://primeng.org/overlaypanel) to recreate a dynamic avatar user preview
 							with a dropdown menu activated with a click on the avatar image.<br><br>
							• **[wrapperClassNames]** string data used for put CSS classes on the component tag element<br>
							• **[classNames]** string data used for put CSS classes to extend the default CSS compoenent class<br>
							• **[name]** string data used for the name of the user<br>
							• **[email]** string data used for the email of the user<br>
							• **[image]** string data used for the image of the user<br>
							• **[menu]** array data list for show datas using the same structure of [PrimeNg MenuModelItem](https://primeng.org/menumodel).`,
            }
        }
    },
};
export default meta;
const sampleDropdownMenu = [
    { label: 'Profile', icon: PrimeIcons.USER },
    { label: 'Settings', icon: PrimeIcons.WRENCH },
    { label: 'Permissions', icon: PrimeIcons.LOCK },
    { label: 'Support', icon: PrimeIcons.EYE },
    { label: 'Logout', icon: PrimeIcons.SIGN_OUT },
];
export const _UserDropdownComponent = {
    args: {
        wrapperClassNames: '',
        classNames: '',
        name: 'Gabriele',
        email: 'gabriele.morero@gmail.com',
        image: 'https://primefaces.org/cdn/primeng/images/demo/organization/walter.jpg',
        menu: sampleDropdownMenu
    },
};
